import '../../src/App.css'
export default function HomePage() {
    const [products, set_products] = useState([]);
    const [categories, set_categories] = useState([]); 
     useEffect(() => {
    async function doSomeDataFetching() {
      console.log("I'm gonna fetch some data!");

      const res = await axios.get(
        "http://localhost:4000/products"
      );

      console.log("Got back:", res);
      set_products(res.data);
    }
    doSomeDataFetching();
  }, []);

  useEffect(() => {
    async function fetchCategories() {
      console.log("I'm gonna fetch some categories!");


      const res = await axios.get(
        "http://localhost:4000/categories"
      );

      console.log("Got back:", res);
      set_categories(res.data);
    }
    fetchCategories();
  }, []);

  //console.log(products);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const handleChange = event => {
    setSearchTerm(event.target.value);
  };
  useEffect(() => {
     async function search() {
       const results = products.filter(product =>
      product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setSearchResults(results);
    }
    search();
  }, [searchTerm]);

  return (
    <table>
      <tr>
        <td>
          <input
               type="text"
                placeholder="Search"
                value={searchTerm}
                onChange={handleChange}
          />
        <ul>
             {searchResults.map(item => (
              <Link to= {`/product/${item.id}`}><li>{item.name}</li></Link>
        ))}
        </ul>
        </td>
        <td>
          <AliceCarousel autoPlay autoPlayInterval="3000">
           {products.map(slide => (

               <img src ={slide.imageUrl} className="sliderimg" alt={slide.name} />

           ))} 
           {console.log(products)}
           </AliceCarousel>

        </td>
      </tr>
      <td>

             {!Array.isArray(categories) ? <div>Loading</div>: (categories.map(cat => {
                return(

               <td style ={{paddingTop: "0px",  display: "flex",
  flexDirection: "column"}}>


                         <Category key={cat.id} category={cat} />
                   </td>  

                )
            }))}

       </td> 
       <td>
         <tr>
            {(products.map((product,i)=> {
                return(

          <td id="bor_panel">
              <Product key={i} product={product}/>
             </td> 

                )
            }))}
        </tr>

      </td>
    </table>
