import axios from "axios";
import { useEffect, useState } from "react";
import Product from "../components/Product";
import { Link } from "react-router-dom";
import '../../src/App.css'
export default function HomePage() {
    const [products, set_products] = useState([]);
    const [categories, set_categories] = useState([]); 
     useEffect(() => {
    async function doSomeDataFetching() {
      console.log("I'm gonna fetch some data!");

      // Getting back data from the net, through the wire, air, and the ocean:
      const res = await axios.get(
        "https://anddigital.herokuapp.com/products"
      );

      console.log("Got back:", res);
      set_products(res.data);
    }
    doSomeDataFetching();
  }, []);

  useEffect(() => {
    async function fetchCategories() {
      console.log("I'm gonna fetch some categories!");

      // Getting back data from the net, through the wire, air, and the ocean:
      const res = await axios.get(
        "https://anddigital.herokuapp.com/categories"
      );

      console.log("Got back:", res);
      set_categories(res.data);
    }
    fetchCategories();
  }, []);
  return (
      <table>
        <tr>
          <th>Categories</th>

          <th>Products</th>
          </tr>

            <td>Search Bar</td>
             <td class="category-list" style ={{padding: "0 155px", verticalAlign: "baseline"}} >
          {!Array.isArray(categories) ? <div>Loading</div>: (categories.map(cat => {
                return(

                  <tr>

                         <td> <Link to="/categories">{cat.title} </Link> </td>

                    </tr>     

                )
            }))}
            </td>
             <tr>

            <td style={{
                    display: "flex",
                    flexWrap: "wrap",
                    margin: "0 -10px",
                  }}>

            {(products.map((product,i)=> {
                return(
                         <Product key={i} product={product}/>
                )
            }))}


           </td> 
           </tr>

      </table>

  )


}

