import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function ProductDetailPage() {

      const { productId } = useParams();
    const [product, set_product] = useState([]);
     useEffect(() => {
    async function fetchProduct() {
      console.log("I'm gonna fetch some data!");

      // Getting back data from the net, through the wire, air, and the ocean:
      const res = await axios.get(
        `https://anddigital.herokuapp.com/products/${productId}`
      );

      console.log("Bu ne:", res);

      set_product(res.data);
    }

    fetchProduct();
  }, [productId]);
  return <div style ={{ display: "block",
  marginLeft: "auto",
  marginRight: "auto"}} >
      Product details
        <div>
      {product ? (
        <>
          <h2 >{product.name}</h2>

          <div style ={{margin: "margin: auto",
 textAlign: "center",
  width:" 50%",
  border: "3px solid red",
  padding: "40px"}}>
            <img alt={product.name} src={product.imageUrl} style={{
        width: "80%",
        padding: "10px",
        boxSizing: "border-box",
      }}/>
            <div >
              <h3>Details</h3>
              <p>{product.description}</p>
              <h3>Price</h3>
              <p>{product.price}</p>
              <h3>Stock Details</h3>
              {product.inStock ? (<p>Available</p>) : (<p>Not Available</p>)}




            </div>
          </div>
        </>
      ) : (
        <p>Loading....</p>
      )}
    </div>
  </div>;
}
